import React from "react";
import styled from 'styled-components'

const Image = (props) => {

  const {
    src,
  } = props;

  const styles = {
    src: src,
  };

  return (
    <React.Fragment>
      <Thumb {...styles}/>
    </React.Fragment>
  )


}

Image.defaultProps = {
  src: "data:image/png;base64,",
};

const Thumb = styled.div`
  background-image: url("${(props) => props.src}");
  background-size: cover;
  width: 430px;
  height: 552px;
`;

export default Image;